import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { useRecoilState } from "recoil";

import List from '@mui/material/List';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';
import MoreHorizIcon from '@mui/icons-material/MoreHoriz';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import Stack from '@mui/material/Stack';
import Box from '@mui/material/Box';


import { followData } from "../atoms/todoData";

import Followings from "./Followings";
import "../stylesheets/Follow.css";

//  ** 할일 **
// 유저 버튼 -> 언팔로우 시 => isfollowing:false 로 변경 
// 각 유저 클릭 -> 해당 유저 feed로 이동 


export default function Following() {


    const [open, setOpen] = React.useState(false);

    const handleClickOpen = () => {
      setOpen(true);
    };
    const handleClose = () => {
      setOpen(false);
    };


 
/* atom 시작 */
let [following, setFollowing] = useRecoilState(followData);// 팔로우data


let [editFollowing, setEditFollowing] = useState([]);

const handleFollowingChange = () => {
    const newFollowData = replaceFollowData({...following, isFollowing: !following.isFollowing});
      
      console.log("Unfollowed")
      
      setFollowing(newFollowData);
      
      setOpen(false);
      
  };

  function  replaceFollowData() {
      
      const thisData = [...following]; // goal State 원본 카피
      let unFollowing = thisData.filter(data =>  data.isFollowing == true
      )
      setFollowing(unFollowing) ;//setGoal를 이용해 state 변경
      console.log(unFollowing)
      window.location.replace(`/exploreSearch/`);//목표로 돌아가기
  }





    return (
        <Box 
            sx={{ width: '100%', bgcolor: 'background.paper' }} 
            textAlign='center' 
            style={{ position:'relative'}}>

        
            <List
        
                sx={{ width: '100%'}}
                style={{position:'relative'}} 
        
                component="div" disablePadding>
        
           

                {/* Button click 효과 및 css 수정 */}
                 <div className="container" > 
                    {/* 왜 div 하나 차이로 레이아웃이 달라지는지 모르겠음 */}
                    <ListItemButton>
                        <Stack>
                            <ListItemText
                                className="button">   
                                {following.strUserName}
                            </ListItemText> 
                
                             {data.dtTodo.map(e => {
                                    return e.ynComplete === "Y" ? 
                                     <React.Fragment>
                                            <ListItemText
                                              secondary={e.todo_title}>  
                                         </ListItemText> 
                                        </React.Fragment> : null}
                             )}

                        </Stack>
                    </ListItemButton> 

                     <Button  className="button1" onClick={handleClickOpen}>
                         <MoreHorizIcon color="disabled"/> 
                     </Button>
                    </div>

                    {/* Dialog 위치 조정, height 등 style 변경 */}
                    <Dialog
                     sx={{ '& .MuiDialog-paper': { width: '80%', maxHeight: 432 } }}
                     maxWidth="sm"
                        open={open}
                        >
                     <Stack spacing={1} >
                         <Button autoFocus onChange={handleFollowingChange} color="error">언팔로우</Button>
                         <Button onClick={handleClose}>취소</Button>
                     </Stack>
               
                    </Dialog>

                </List>
                
            
     
        </Box>
    
    )} ;
