]
console.log(Data);

export default function Search() {

    const styleInfo = {
      paddingRight:'10px',
      position:'relative',
      left:'10vh'
      };
  
    /* 
    const [filterFn, setFilterFn] = useState({ fn: items => { return items; } })
    
    const handleSearch = e => {
      let target = e.target;
      setFilterFn({
          fn: items => {
              if (target.value == "")
                  return items;
              else
                  return items.filter(x => x.fullname.toLowerCase().includes(target.value))
          }
      })
    } */

    const [searchTerm, setSearchTerm] = useState("");
  
    
      
      /* <div>
        <ul>
          <li style={{position:'relative',left:'10vh'}}>
            <span style={styleInfo}>
                {data.strUserName}<br/>
                {data.title}</span>
          </li>
        </ul>
      </div> */

      
    
    return (
        <Box 
        sx={{ width: '100%', bgcolor: 'background.paper' }} 
        textAlign='center' 
        style={{ position:'relative'}}>
        
          {/* - ì…ë ¥ë°›ëŠ” stringì— ë§¤ì¹˜ë˜ëŠ” ì•„ì´ë””,í• ì¼,email ë¦¬ìŠ¤íŠ¸ë¡œ ë¶ˆëŸ¬ì™€ ë³´ì—¬ì£¼ê¸°
                ë°›ì•„ì™€ì•¼ í•˜ëŠ” ê°’ 
                -user nickname
                -ë§¤ì¹˜ëœ í• ì¼ ì˜ ë‹¨ì–´
                -ì°¸ê³ ) ì„¤ì •í•œ ìƒ‰  
            - style ìˆ˜ì •ì‚¬í•­
                -search ê²€ìƒ‰ input ë†’ì´ ì¡°ì ˆ
                -í•­ëª© ì„ íƒì‹œ default ìƒ‰ ë³€ê²½ */}
            <TextField 
                fullWidth  
                variant="filled"
                size="small"
                id="filled-basic"
                placeholder="email ,ID ë˜ëŠ” í•  ì¼ ê²€ìƒ‰"
                sx={{ boxShadow: 'none'}}    
                onChange={(event) => {
                  setSearchTerm(event.target.value);
                }}
                
                InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <SearchIcon fontSize="small" />
                  </InputAdornment>
                ),
              }}
            >
            </TextField>
            

            {Data.filter((data)=> {
              if (searchTerm == "") {
                return data
              } else if (data.strUserName.toLowerCase().includes(searchTerm.toLowerCase())) {
                return data
              }
            }).map(data=>{
              return(
                <ListItem style={{position:'relative'}} key={data.todo_id} component="div" disablePadding>
                  <ListItemButton>
                    <ListItemText 
                    primary={data.strUserName}
                    secondary={data.title} />
                   </ListItemButton>
                </ListItem>
               );

              })}
                
        </Box>
    
    )}
