import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { useRecoilState } from "recoil";

import Box from '@mui/material/Box';
import List from '@mui/material/List';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';
import MoreHorizIcon from '@mui/icons-material/MoreHoriz';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import Stack from '@mui/material/Stack';

import { followData } from "../atoms/todoData";

import "../stylesheets/Follow.css";

//  ** 할일 **
// 유저 버튼 -> 언팔로우 시 => isfollowing:false 로 변경 
// 각 유저 클릭 -> 해당 유저 feed로 이동 


export default function Following() {


    const [open, setOpen] = React.useState(false);

    const handleClickOpen = () => {
      setOpen(true);
    };
    const handleClose = () => {
      setOpen(false);
    };




/* atom 시작 */
let [following, setFollowing] = useRecoilState(followData);// 팔로우data



let [editFollowingState, setEditFollowingState] = useState(followData.isFollowing);

/*
useEffect(()=>{
    let originFollowSate = [...following];
    let originFollow;
    originFollow = originFollowSate.isFollowing//원본 follow 가지고 옴
    setEditFollowingState(originFollow)
},[])
*/

//function unFollowing(isFollowing) {
   // setEditFollowingState({editFollowingState, isFollowing})
//}

const handleFollowingChange = (e) => {
    const isFollowing = false;
    //const isFollowing = editFollowingState.target.value;
    // const new_goal_item = {...editGoalState};       
    // new_goal_item.title_color = e.target.value;
    // setEditGoalState(new_goal_item);
    setEditFollowingState();
    setFollowing({...following, isFollowing:isFollowing})
    setOpen(false)
    
};
//setOpen(false)






    return (
        <Box 
            sx={{ width: '100%', bgcolor: 'background.paper' }} 
            textAlign='center' 
            style={{ position:'relative'}}>

            {following.products.map(data => {
                return (
                    <List
                        sx={{ width: '100%'}}
                        style={{position:'relative'}} 
                        key={data.strUserId} component="div" disablePadding>
                            
                                {/* isfollowing 값을 true/false ? -> 문법 오류 해결 */}
                                {data.isFollowing  ? (
                                 <React.Fragment>
                                {/* Button click 효과 및 css 수정 */}
                                    <div className="container"> 
                                    {/* 왜 div 하나 차이로 레이아웃이 달라지는지 모르겠음 */}
                                     <ListItemButton>
                                        <Stack>
                                            <ListItemText
                                                className="button"
                                                primary={data.strUserName}>   
                                            </ListItemText> 
                                        
                                                {data.dtTodo.map(e => {
                                                    return e.ynComplete === "Y" ? 
                                                        <React.Fragment key={e.goal_id}>
                                                            <ListItemText
                                                                secondary={e.todo_title}>  
                                                            </ListItemText> 
                                                        </React.Fragment> : null}
                                                )}
                                            
                                        </Stack>
                                     </ListItemButton> 

                                        <Button  className="button1" onClick={handleClickOpen}>
                                            <MoreHorizIcon color="disabled"/> 
                                        </Button>
                                    </div>

                                    {/* Dialog 위치 조정, height 등 style 변경 */}
                                    <Dialog
                                        sx={{ '& .MuiDialog-paper': { width: '80%', maxHeight: 432 } }}
                                        maxWidth="sm"
                                        open={open}
                                        >
                                        <Stack spacing={1} >
                                            <Button autoFocus onClick={handleFollowingChange} color="error">언팔로우</Button>
                                            <Button onClick={handleClose}>취소</Button>
                                        </Stack>
                                       
                                    </Dialog>
                                </React.Fragment>
                                ) : null}
                            
                         
                            
                    </List>
                );
            })}
     
        </Box>
    
    )}
