import React, { useState } from 'react'

import { Button, TextField } from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import InputAdornment from '@mui/material/InputAdornment';
import InputBase from '@mui/material/InputBase';
import { alpha, styled } from '@mui/material/styles';

import '../../stylesheets/Search.css';

//  ** í• ì¼ **
// âœ” filter í•¨ìˆ˜ : ì…ë ¥ë°›ëŠ” stringì— ë§¤ì¹˜ë˜ëŠ” ì•„ì´ë”” ë¶ˆëŸ¬ì™€ ë³´ì—¬ì£¼ê¸°
// ìœ ì € ë¦¬ìŠ¤íŠ¸-> ìœ ì € í´ë¦­ -> ìœ ì € feed ì´ë™ -> íŒ”ë¡œìš° ë²„íŠ¼ -> event: isfollowing: true ìƒì„± ë˜ëŠ” ë³€ê²½ 

// ë°›ì•„ì™€ì•¼ í•˜ëŠ” ê°’ 
// -user id ì…ë ¥-> filter -> ì¶œë ¥ user nickname 

// < style ìˆ˜ì •ì‚¬í•­ >
// -search ê²€ìƒ‰ input ë†’ì´ ì¡°ì ˆ
// -í•­ëª© ì„ íƒì‹œ default ìƒ‰ ë³€ê²½ 

const RedditTextField = styled((props) => (
  <TextField InputProps={{ disableUnderline: true }} {...props} />
))(({ theme }) => ({
  '& .MuiFilledInput-root': {
    overflow: 'hidden',
    borderRadius: 4,
    backgroundColor: theme.palette.mode === 'light' ? '#f5f5f5' : '#f5f5f5',
    transition: theme.transitions.create([
      'border-color',
      'background-color',
      'box-shadow',
    ]),
    '&:hover': {
      backgroundColor: '#f5f5f5',
    },
    '&.Mui-focused': {
      backgroundColor: '#f5f5f5',
    },
  },
}));

const BootstrapInput = styled(InputBase)(({ theme }) => ({
  'label + &': {
    marginTop: theme.spacing(3),
  },
  '& .MuiInputBase-input': {
    borderRadius: 4,
    position: 'relative',
    backgroundColor: theme.palette.mode === 'light' ? '#f5f5f5' : '#f5f5f5',
    fontSize: 13,
    color: '#c4c0c0',
    width: 'fullwidth',
    padding: '10px 12px',
    transition: theme.transitions.create([
      'border-color',
      'background-color',
      'box-shadow',
    ]),
    // Use the system font instead of the default Roboto font.
    fontFamily: [
      '-apple-system',
      'BlinkMacSystemFont',
      '"Segoe UI"',
      'Roboto',
      '"Helvetica Neue"',
      'Arial',
      'sans-serif',
      '"Apple Color Emoji"',
      '"Segoe UI Emoji"',
      '"Segoe UI Symbol"',
    ].join(','),
    '&:focus': {
    },
  },
}));

const CssTextField = styled(TextField)({
  '& label.Mui-focused': {
    color: '#f5f5f5',
  },
  '& .MuiInput-underline:after': {
    borderBottomColor: '#f5f5f5',
  },
  '& .MuiOutlinedInput-root': {
    '& fieldset': {
      borderColor: '#f5f5f5',
    },
    '&:hover fieldset': {
      borderColor: '#f5f5f5',
    },
    '&.Mui-focused fieldset': {
      borderColor: '#f5f5f5',
    },
    '& .MuiFilledInput-root': {
      backgroundColor: '#f5f5f5',
    },
  },
  '& .MuiFilledInput-root': {
    overflow: 'hidden',
    borderRadius: 4,
    backgroundColor: theme.palette.mode === 'light' ? '#f5f5f5' : '#f5f5f5',
    transition: theme.transitions.create([
      'border-color',
      'background-color',
      'box-shadow',
    ]),
    '&:hover': {
      backgroundColor: '#f5f5f5',
    },
    '&.Mui-focused': {
      backgroundColor: '#f5f5f5',
    },
  },
});

const Data = [
  { "strUserName": "ì£¼ì˜", "todo_id": "0", "title": "ğŸ¥›ìš°ìœ ë§ˆì‹œê¸°" },
  { "strUserName": "ğŸˆë©”ë¡œë‚˜", "todo_id": "1", "title": "ê³µë¶€í•˜ê¸°" },
  { "strUserName": "ë‘ë‘", "todo_id": "5", "title": "ìŠ¤í„°ë””ì¹´í˜ê°€ê¸°" },
  { "strUserName": "ë‚˜ì•¼", "todo_id": "7", "title": "8ì‹œê°„ ìê¸°" }
]
//console.log(Data);

export default function Search() {

    const [searchTerm, setSearchTerm] = useState("");
  
    return (
      <div className="search-search-list-wrap" >
            {/* textfield underline ìƒ‰ìƒ ë³€ê²½ */}
            <RedditTextField
              fullWidth  
              hiddenLabel
              size="small"
              
              placeholder="email ,ID ê²€ìƒ‰"
              sx={{ boxShadow: 'none'}}  
              id="reddit-input"

              onChange={(event) => {
                setSearchTerm(event.target.value);
              }}
      
              InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <SearchIcon fontSize="small" className="search-list-icon" />
                </InputAdornment>
              ),
            }}
              
              />
            <BootstrapInput defaultValue="email ,ID ê²€ìƒ‰" id="bootstrap-input" >
            </BootstrapInput>
            <CssTextField label="Custom CSS" id="custom-css-outlined-input" size="small" />
            <TextField 
                fullWidth  
                hiddenLabel
                id="filled-basic"
                size="small"
                variant="filled"
                placeholder="email ,ID ê²€ìƒ‰"
                sx={{ boxShadow: 'none'}}    
                onChange={(event) => {
                  setSearchTerm(event.target.value);
                }}
        
                InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <SearchIcon fontSize="small" className="search-list-icon" />
                  </InputAdornment>
                ),
              }}
            >
            </TextField>

            {Data.filter((data)=> {
              if (searchTerm === "") {
                return data
              } else if (data.strUserName.toLowerCase().includes(searchTerm.toLowerCase())) {
                return data
              } else if (data.title.toLowerCase().includes(searchTerm.toLowerCase())) {
                return data
              }
            }).map(data=>{
              return(
                <React.Fragment key={data.todo_id}>
                  <div className="search-list-box" key={data.todo_id}>
                    <Button className="search-list-button" id={data.todo_id} name={data.todo_id}>
                        <div className="search-list-name" id={data.strUserName} name={data.strUserName}>
                          <p>{data.strUserName}</p>
                        </div>
                        <div className="search-list-text" id={data.title} name={data.title}>
                          <p>{data.title}</p>
                        </div>
                    </Button>
                  </div>

                </React.Fragment>
               
               );

              })}
                
    
      </div>
    
    )}
