import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { useRecoilState } from "recoil";

import List from '@mui/material/List';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';
import MoreHorizIcon from '@mui/icons-material/MoreHoriz';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import Stack from '@mui/material/Stack';

import "../stylesheets/Follow.css";


export default function Followings({ data }) {

    console.log(data.isFollowing);

    const [isFollow, setIsFollow] = useState(data.isFollowing);

    const [open, setOpen] = React.useState(false);

    const handleClickOpen = () => {
      setOpen(true);
    };
    const handleClose = () => {
      setOpen(false);
    };

    const handleFollowingChange = () => {
    
        //const isFollowing = editFollowingState.target.value;
        // const new_goal_item = {...editGoalState};       
        // new_goal_item.title_color = e.target.value;
        // setEditGoalState(new_goal_item);
        
        setIsFollow(!isFollow);
        setOpen(false)
        
    };


    return (

        <List
        sx={{ width: '100%'}}
        style={{position:'relative'}} 
        key={data.strUserId}
        component="div" disablePadding>
    
                {/* Button click 효과 및 css 수정 */}
                 <div className="container"> 
                    {/* 왜 div 하나 차이로 레이아웃이 달라지는지 모르겠음 */}
                    <ListItemButton>
                        <Stack>
                            <ListItemText
                                className="button"
                                >   
                                {data.isFollowing && data.strUserName}
                            </ListItemText> 
                
                             {data.dtTodo.map(e => {
                                    return e.ynComplete === "Y" ? 
                                     <React.Fragment key={e.goal_id}>
                                            <ListItemText
                                              secondary={e.todo_title}>  
                                         </ListItemText> 
                                        </React.Fragment> : null}
                             )}

                        </Stack>
                    </ListItemButton> 

                     <Button  className="button1" onClick={handleClickOpen}>
                         <MoreHorizIcon color="disabled"/> 
                     </Button>
                    </div>

                    {/* Dialog 위치 조정, height 등 style 변경 */}
                    <Dialog
                     sx={{ '& .MuiDialog-paper': { width: '80%', maxHeight: 432 } }}
                     maxWidth="sm"
                        open={open}
                        >
                     <Stack spacing={1} >
                         <Button autoFocus onClick={handleFollowingChange} color="error">언팔로우</Button>
                         <Button onClick={handleClose}>취소</Button>
                     </Stack>
               
                    </Dialog>
             
             
    
 
    
        </List>

    )
};
