import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { useRecoilState } from "recoil";

import Box from '@mui/material/Box';
import List from '@mui/material/List';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';
import MoreHorizIcon from '@mui/icons-material/MoreHoriz';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import Stack from '@mui/material/Stack';

import { followData } from "../atoms/todoData";

import "../stylesheets/Follow.css";

//  ** 할일 **
// 팔로잉 추가 함수
// 팔로잉 삭제 함수 


export default function Follower() {


    const [open, setOpen] = React.useState(false);

    const handleClickOpen = () => {
      setOpen(true);
    };
    const handleClose = () => {
      setOpen(false);
    };




/* atom 시작 */
let [following, setFollowing] = useRecoilState(followData);// 팔로우data

let { originID } = useParams();//url Params 따오기 ???

let [editFollowingState, setEditFollowingState] = useState('');

/*useEffect(()=>{
    let originFollowingState = [...following]; //원본 follow 가지고 옴
    let originFollowing;
    originFollowing = originFollowingState[originID]
    setFollowingState(originFollowing)
},[])*/



    return (
        <Box 
            sx={{ width: '100%', bgcolor: 'background.paper' }} 
            textAlign='center' 
            style={{ position:'relative'}}>

            {following.map(data => {
                return (
                    <List
                        style={{position:'relative'}} 
                        key={data.strUserId} component="div" disablePadding>
                            
                                {/* isfollowing 값을 true/false ? -> 문법 오류 해결 */}
                                {data.isFollower  ? (
                                 <span >
                                {/* Button click 효과 및 css 수정 */}
                                    <div className="container"> 
                                    {/* 왜 div 하나 차이로 레이아웃이 달라지는지 모르겠음 */}
                                     <ListItemButton>
                                        <Stack>
                                            <ListItemText
                                                className="button"
                                                primary={data.strUserName}>   
                                            </ListItemText> 
                                        
                                            <ListItemText>
                                                
                                                {data.dtTodo.map(e => {
                                                    return(
                                                        <p key={e.goal_id}>
                                                             <Stack>
                                                                {e.ynComplete === "Y" ? (
                                                                    <li>
                                                                    {e.todo_title}
                                                                    </li>) : null}
                                                            </Stack>
                                                        </p>)
                                                })}
                                            </ListItemText> 
                                        </Stack>
                                     </ListItemButton> 

                                        <Button  className="button1" onClick={handleClickOpen}>
                                            <MoreHorizIcon color="disabled"/> 
                                        </Button>
                                    </div>

                                    {/* Dialog 위치 조정, height 등 style 변경 */}
                                    <Dialog
                                        sx={{ '& .MuiDialog-paper': { width: '80%', maxHeight: 432 } }}
                                        maxWidth="sm"
                                        open={open}
                                        >
                                        <Stack spacing={1} >
                                            <Button autoFocus onClick={handleClose} color="error">언팔로우</Button>
                                            <Button onClick={handleClose}>취소</Button>
                                        </Stack>
                                       
                                    </Dialog>
                                 </span>
                                ) : null}
                            
                         
                            
                    </List>
                );
            })}
     
        </Box>
    
    )}
