import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { useRecoilState } from "recoil";

import Box from '@mui/material/Box';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';
import MoreHorizIcon from '@mui/icons-material/MoreHoriz';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import Stack from '@mui/material/Stack';

import { followData } from "../atoms/todoData";


//함수선언
// isfollowing? Y인 사람만 리스트에 불러와져 있으면 됨. 
//  -> 더미 데이터에서 isfollowing 이 y인 사람만 파싱? 으로 처리 하여 
//     나타나는 것은 닉네임으로 표현하기 
// 팔로잉 추가 함수
// 팔로잉 삭제 함수 


export default function Following() {


    const [open, setOpen] = React.useState(false);

    const handleClickOpen = () => {
      setOpen(true);
    };
    const handleClose = () => {
      setOpen(false);
    };




/* atom 시작 */
let [following, setFollowing] = useRecoilState(followData);// 목표goals 아이템

let { originID } = useParams();//url Params 따오기

let [editFollowingState, setEditFollowingState] = useState('');

/*useEffect(()=>{
    let originFollowingState = [...following]; //원본 follow 가지고 옴
    let originFollowing;
    originFollowing = originFollowingState[originID]
    setFollowingState(originFollowing)
},[])*/

    return (
        <Box 
            sx={{ width: '100%', bgcolor: 'background.paper' }} 
            textAlign='center' 
            style={{ position:'relative'}}>

            {following.map(data=>{
                return (
                    <ListItem 
                        style={{position:'relative'}} 
                        key={data.strUserId} component="div" disablePadding>
                            <ListItemButton>
                                {data.isFollowing > -1 ? (
                                 <span >
                                    <Stack direction="row" spacing={1}>
                                         <ListItemText 
                                            primary={data.strUserName}
                                            secondary={data.strUserInfo} />
    
                                        <Button onClick={handleClickOpen}>
                                            <MoreHorizIcon color="disabled"/> 
                                         </Button>
                                     </Stack>
                                    {/* Dialog 위치 조정, height... style 변경 */}
                                    <Dialog
                                        sx={{ '& .MuiDialog-paper': { width: '80%', maxHeight: 432 } }}
                                        maxWidth="sm"
                                        open={open}
                                        >
                                        <Stack spacing={1} >
                                            <Button autoFocus onClick={handleClose} color="error">언팔로우</Button>
                                            <Button onClick={handleClose}>취소</Button>
                                        </Stack>
                                       
                                    </Dialog>
                                    </span>
                                ) : null}
                            </ListItemButton>
                         
                            
                    </ListItem>
                );
            })}
     
        </Box>
    
    )}
