import React, { useState } from 'react'
import Box from '@mui/material/Box';
import { TextField } from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import InputAdornment from '@mui/material/InputAdornment';

import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';

/* 
< Ìï†Ïùº >
- ‚úî filter Ìï®Ïàò
    ÏûÖÎ†•Î∞õÎäî stringÏóê Îß§ÏπòÎêòÎäî ÏïÑÏù¥Îîî, Ìï†Ïùº, email Î¶¨Ïä§Ìä∏Î°ú Î∂àÎü¨ÏôÄ Î≥¥Ïó¨Ï£ºÍ∏∞
- Î∂àÎü¨ÏôÄÏßÑ Îç∞Ïù¥ÌÑ∞(userÏ†ïÎ≥¥)ÏóêÏÑú user click -> user feedÎ°ú Ïù¥Îèô 

< Î∞õÏïÑÏôÄÏïº ÌïòÎäî Í∞í > 
-user id -> user nickname 

< style ÏàòÏ†ïÏÇ¨Ìï≠ >
-search Í≤ÄÏÉâ input ÎÜíÏù¥ Ï°∞Ï†à
-Ìï≠Î™© ÏÑ†ÌÉùÏãú default ÏÉâ Î≥ÄÍ≤Ω 
*/

const Data = [
  { "strUserName": "Ï£ºÏòÅ", "todo_id": "0", "title": "ü•õÏö∞Ïú†ÎßàÏãúÍ∏∞" },
  { "strUserName": "üçàÎ©îÎ°úÎÇò", "todo_id": "1", "title": "Í≥µÎ∂ÄÌïòÍ∏∞" },
  { "strUserName": "ÎëêÎëê", "todo_id": "5", "title": "Ïä§ÌÑ∞ÎîîÏπ¥ÌéòÍ∞ÄÍ∏∞" },
  { "strUserName": "ÎÇòÏïº", "todo_id": "7", "title": "8ÏãúÍ∞Ñ ÏûêÍ∏∞" }
]
console.log(Data);

export default function Search() {

    const [searchTerm, setSearchTerm] = useState("");
  
    return (
        <Box 
        sx={{ width: '100%', bgcolor: 'background.paper' }} 
        textAlign='center' 
        style={{ position:'relative'}}>
        
            <TextField 
                fullWidth  
                variant="filled"
                size="small"
                id="filled-basic"
                placeholder="email ,ID Í≤ÄÏÉâ"
                sx={{ boxShadow: 'none'}}    
                onChange={(event) => {
                  setSearchTerm(event.target.value);
                }}
                
                InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <SearchIcon fontSize="small" />
                  </InputAdornment>
                ),
              }}
            >
            </TextField>
            

            {Data.filter((data)=> {
              if (searchTerm === "") {
                return data
              } else if (data.strUserName.toLowerCase().includes(searchTerm.toLowerCase())) {
                return data
              } else if (data.title.toLowerCase().includes(searchTerm.toLowerCase())) {
                return data
              }
            }).map(data=>{
              return(
                <ListItem style={{position:'relative'}} key={data.todo_id} component="div" disablePadding>
                  <ListItemButton>
                    <ListItemText 
                      primary={data.strUserName}
                      secondary={data.title} />
                   </ListItemButton>
                </ListItem>
               );

              })}
                
        </Box>
    
    )}
