import React from 'react';
import { useState } from "react";
import { useForm } from "react-hook-form";
import '../stylesheets/Signin.css';
import { TextField } from '@mui/material';
import { Link } from 'react-router-dom';


function Join(){

    const { register, handleSubmit, errors, watch } = useForm({ mode: "onChange" });
    const [result, setResult] = useState("");
    const onSubmit = (data) => {
      setResult(JSON.stringify(data));
      console.log(result)
    }
    const onError = (error) => {
      console.log(error);
    };
    console.log(watch())

    return (

    <form onSubmit={handleSubmit(onSubmit,onError)}>
    <TextField
      {...register("userId")} //값을 불러오기 위한 이름
        id="standard-email-input"
        label="이메일"
        type="email"
        autoComplete="current-email"
        variant="standard"
        helperText="Please enter Email"
        margin="dense"
        fullWidth 
      />
      <TextField
       {...register("userPwd", {minLength:{ value: 5, message: "비밀번호는 5자 이상으로 입력해주세요."}})}  //값을 불러오기 위한 이름
        id="standard-password-input"
        label="비밀번호"
        type="password"
        autoComplete="current-password"
        variant="standard"
        helperText="Please enter Password"
        margin="dense"
        fullWidth 
      />
      <input className="join_btn" type="submit" value={"확인"}/>
      
      <div className='join_txt'>
          <p>
          가입하면: <br />
          • 다른 기기에서 로그인 가능합니다.<br />
          • 다른 사용자들과 함께 할 수 있습니다.
          </p>
          <p>가입 시 다음 사항에 동의하는 것으로 간주합니다. &nbsp;
          {/* 
           <Link to="/text/use" className='termOfUse'><a value="0" >이용약관</a></Link>과 
          <Link className='policy' to="/text/policy"><a value="1">개인정보 정책</a></Link> */}
          <Link to="/text/use" className='termOfUse'><a value="0" >이용약관</a></Link>과 
          <Link className='policy' to="/text/policy"><a value="1">개인정보 정책</a></Link>
          </p>
      </div>
      </form>
)

}

export default Join;
